// ------------------------
// LOGIN
// ------------------------
WebUI.callTestCase(findTestCase('Login_success'), [:], FailureHandling.STOP_ON_FAILURE)

WebUI.delay(2)

// ------------------------
// ĐI TỚI TRANG THÔNG TIN KHÁCH HÀNG
// ------------------------
WebUI.click(findTestObject('Customer/button_quan_ly_tai_khoan'))

WebUI.verifyElementText(findTestObject('Customer/button_thong_tin_khach_hang'), 'Thông tin khách hàng')

WebUI.click(findTestObject('Customer/button_thong_tin_khach_hang'))

// ------------------------
// STEP 3: Tìm kiếm theo tên
// ------------------------
String keyword = 'Đỗ Quốc Thành'

WebUI.setText(findTestObject('Customer/TC_find/Page_HyperS/input_key_find'), keyword)

WebUI.sendKeys(findTestObject('Customer/TC_find/Page_HyperS/input_key_find'), Keys.chord(Keys.ENTER))

WebUI.waitForPageLoad(5)

WebUI.delay(1)

// Lấy danh sách dòng sau khi tìm
List<WebElement> rowsAfterSearch = WebUiCommonHelper.findWebElements(findTestObject('Customer/TC_find/Page_HyperS/rowItem'), 10)

println('➤ Số lượng kết quả sau tìm kiếm: ' + rowsAfterSearch.size())

if (rowsAfterSearch.size() == 0) {
    KeywordUtil.markFailed("FAILED: Không có kết quả nào khi tìm '$keyword'")

    return null
}

// ------------------------
// STEP 5: Click filter “Tổng số lượng tài khoản” để reset filter mặc định
// ------------------------
WebUI.click(findTestObject('Customer/button_tong_acc'))

WebUI.delay(1)

// ------------------------
// STEP 6: Click filter “Vô hiệu hóa”
// ------------------------
WebUI.click(findTestObject('Customer/button_loc_vo_hieu_hoa'))

WebUI.waitForPageLoad(5)

WebUI.delay(1)

// Lấy danh sách dòng sau khi filter
List<WebElement> rowsAfterFilter = WebUiCommonHelper.findWebElements(findTestObject('Customer/TC_find/Page_HyperS/rowItem'), 10)

println('➤ Số lượng kết quả sau khi FILTER (Vô hiệu hóa): ' + rowsAfterFilter.size())

// ------------------------
// VALIDATION LOGIC 1: Nếu danh sách rỗng → PASS (không ai tên đó bị vô hiệu hóa)
// ------------------------
if (rowsAfterFilter.size() == 0) {
    KeywordUtil.markPassed("PASSED: Không có khách hàng tên '$keyword' đang bị vô hiệu hóa → danh sách rỗng là đúng.")

    return null
}

// ------------------------
// VALIDATION LOGIC 2: Kiểm tra tất cả dòng đều chứa tên cần tìm
// ------------------------
boolean nameValid = true

for (WebElement row : rowsAfterFilter) {
    String rowText = row.getText().trim()

    if (!(rowText.contains(keyword))) {
        nameValid = false

        println('❌ Dòng không chứa tên cần tìm: ' + rowText)

        break
    }
}

if (!(nameValid)) {
    KeywordUtil.markFailed("FAILED: Có dòng không chứa tên '$keyword' sau khi tìm kiếm + lọc.")

    return null
}

// ------------------------
// VALIDATION LOGIC 3: Kiểm tra trạng thái = “Vô hiệu”
// ------------------------
boolean statusValid = true

for (WebElement row : rowsAfterFilter) {
    String status = row.findElement(By.xpath('.//div[contains(@class,"status")]')).getText().trim()

    if (!(status.equalsIgnoreCase('Vô hiệu hóa'))) {
        statusValid = false

        println('❌ Dòng có trạng thái sai: ' + status)

        break
    }
}

if (!(statusValid)) {
    KeywordUtil.markFailed('FAILED: Có dòng không phải trạng thái \'Vô hiệu\' sau khi áp dụng filter.')
} else {
    KeywordUtil.markPassed('PASSED: Tìm kiếm theo tên + lọc \'Vô hiệu\' hoạt động chính xác.')
}

